tinymce.PluginManager.add("embedimage", function (editor, f) {
/**
* Setup buttons for Bdesk Editor
* @lastchange: $arsalanshah
*/
   editor.addButton("embed_image", {
	  icon: 'image',
	  title: 'Image Embed Upload',
      cmd: "embed_image"
    });
/**
* Init the plugin
* @lastchange $arsalanshah
*/ 
   editor.addCommand("embed_image", function () {
        editor.windowManager.open({
 	        //text for dialog							  
            title: "Embed a image",
            width: 450,
			//location:
            height: 80,
		    //we need a to put a base64 encode image into texteditor	
            html: '<input type="file" class="input" name="embedded_image" id="image_embed" style="font-size:14px;padding:30px;" accept="image/x-png, image/gif, image/jpeg"/>',
            buttons: [{
                text: "Insert",
                subtype: "primary",
          /**
          * Triger a OnClick
          * @lastchange: $arsalanshah
          */ 				
               onclick: function () {
                    if (window.File && window.FileReader && window.FileList && window.Blob) {
                        var imagefile = document.getElementsByName("embedded_image")[0].files;                        
						var class_filereader = new FileReader();
                         for (var i = 0, f; f = imagefile[i]; i++) {
                                var filesiz = f.size;
                         }
						class_filereader.onload = function (base64) {
						imgData = base64.target.result;
						var img = new Image();
                        img.src = imgData;
						
                        editor.execCommand("mceInsertContent", false, "<img src='" + imgData + "' />");
						
                   };
		  /**
           * Log errors
           * @lastchange: $arsalanshah
           */		   
                 class_filereader.onerror = function (err) {
                            console.log("error", err);
                            console.log(err.getMessage());
                  };
                  if (imagefile.length > 0) {
                            class_filereader.readAsDataURL(imagefile[0]);
                  } 
				  else {	  
                      alert("No File selected");
                  }} 
				  else {
                        alert("Please change your browser to modern one");
                  }
                     (this).parent().parent().close();
                }}, 
				{
                text: "Close",
                onclick: function () {
                             (this).parent().parent().close();
                          }
                }],
        });
    });
/**
* Setup Insert buttons for Bdesk Editor Dialog
* @lastchange: $arsalanshah
*/ 
     editor.addMenuItem("embedimage", {
        cmd: "embed_image",
        context: "insert",
		text: 'Embed Image',
		icon: 'image',
    });
});
